volatile uint8_t ledState =0;
volatile int countDown =1;
volatile int INTFlag =0;
void TMR1_IRQHandler(void){
	
	if (INTFlag!=0){
		INTFlag--;
		TIMER_ClearIntFlag(TIMER1);
		return;
	}
	countDown -=1;
	if(countDown==0){
		
		INTFlag=0;
		countDown = ledState ? 5 : 8;
		ledState = ~ledState;
		if(ledState){
			PA12=1;
			PA13=0;
			PA14=1;
		}else{
			PA12=1;
			PA13=1;
			PA14=0;
		}
	}
	TIMER_ClearIntFlag(TIMER1);
}
void Init_Timer1(void){
	TIMER_Open(TIMER1,TMR1_OPERATING_MODE,TMR1_OPERATING_FREQ);
	TIMER_EnableInt(TIMER1);
	NVIC_EnableIRQ(TMR1_IRQn);
	TIMER_Start(TIMER1);
}
void EINT1_IRQHandler(){
	INTFlag =5;
	GPIO_CLR_INT_FLAG(PB,BIT15);
}
void Init_EXTINT(){
	GPIO_SetMode(PB,BIT15,GPIO_MODE_INPUT);
	GPIO_EnableEINT1(PB,15,GPIO_INT_RISING);
	NVIC_EnableIRQ(EINT1_IRQn);
	
	GPIO_SET_DEBOUNCE_TIME(GPIO_DBCLKSRC_LIRC,GPIO_DBCLKSEL_64);
	GPIO_ENABLE_DEBOUNCE(PB,BIT15);
}
void callINTfunc(){
	PA12=1;
	PA13=1;
	PA14=0;
	while(INTFlag){
		CloseSevenSegment();
		ShowSevenSegment(3,INTFlag);
		draw_LCD(move[1]);
	}
	PA12=1;
	PA13=0;
	PA14=1;
	CloseSevenSegment();
	INTFlag=0;
}
int main(void)
{
	
	SYS_Init();
	GPIO_SetMode(PA,BIT12,GPIO_MODE_OUTPUT);
	GPIO_SetMode(PA,BIT13,GPIO_MODE_OUTPUT);
	GPIO_SetMode(PA,BIT14,GPIO_MODE_OUTPUT);
	Init_Timer1();
	init_LCD();
	Init_EXTINT();
	while(1){
		
		CloseSevenSegment();
		while(ledState){
			if(INTFlag!=0)callINTfunc();
			else INTFlag=0;
			CloseSevenSegment();
			ShowSevenSegment(0,countDown);
			draw_LCD(move[0]);
			CLK_SysTickDelay(100000);
			draw_LCD(move[1]);
			CLK_SysTickDelay(100000);
			draw_LCD(move[2]);
			CLK_SysTickDelay(100000);
		}
		CloseSevenSegment();
		while(!ledState){
			if(INTFlag !=0)callINTfunc();
			else INTFlag=0;
			CloseSevenSegment();
			ShowSevenSegment(3,countDown);
			draw_LCD(move[1]);
		}
		CloseSevenSegment();
	}
	
	
}